COMMIT;

-- ----------------------------
-- Table structure for `ip_elements`
-- ----------------------------
DROP TABLE IF EXISTS `ip_elements`;
CREATE TABLE `ip_elements` (
`id`  bigint(20) NOT NULL AUTO_INCREMENT ,
`description`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`element_type_id`  bigint(20) NOT NULL COMMENT 'Foreign key to the table ip_element_types' ,
`ip_program_id`  bigint(20) NOT NULL ,
`is_active`  tinyint(1) NOT NULL DEFAULT 1 ,
`active_since`  timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ,
`created_by`  bigint(20) NOT NULL ,
`modified_by`  bigint(20) NOT NULL ,
`modification_justification`  text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL ,
PRIMARY KEY (`id`),
FOREIGN KEY (`element_type_id`) REFERENCES `ip_element_types` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`ip_program_id`) REFERENCES `ip_programs` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
FOREIGN KEY (`created_by`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`modified_by`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
INDEX `FK_element_element_type_idx` (`element_type_id`) USING BTREE ,
INDEX `FK_ip_elements_ip_programs_owner_id` (`ip_program_id`) USING BTREE ,
INDEX `fk_ip_elements_users_created_by` (`created_by`) USING BTREE ,
INDEX `fk_ip_elements_users_modified_by` (`modified_by`) USING BTREE 
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
AUTO_INCREMENT=180

;

