INSERT INTO `ip_project_contribution_overviews` VALUES ('1301', '107', '46', '2014', '', null, '', null, '1', '2016-04-10 18:24:38', '29', '29', ''), ('1302', '107', '47', '2015', '', 'Scoping studies and Planning workshops on ICT and radio to deliver climate services.\r\nPICSA curriculum published and adapted to project context.  \r\nPreliminary lessons from PICSA published.\r\nBaseline and needs assessment surveys published.\r\nPlanning and Review process to assess engagement with rural communities, develop forecast-based plans, ahead of each growing season.', '', 'Baseline and needs assessment disaggregated by gender.  PICSA training and follow-up targeted gender balance.', '1', '2016-04-10 18:24:38', '29', '29', ''), ('1303', '107', '46', '2015', '', 'WMO request to lead capacity development of NMHS failed to make the needed information available in Tanzania, requiring CCAFS to adjust communication strategies to fit available information.  The NMHS of Malawi made incremental progress in downscaling seasonal forecasts in a useful format.', '', 'Needs assessment was disaggregated by gender, but showed that men and women desire the same types of climate information.  Initial efforts to develop information products for use by rural communities therefore did not require explicit gender differences.', '1', '2016-04-10 18:24:38', '29', '29', ''), ('1304', '107', '47', '2016', '', null, '', null, '1', '2016-04-10 18:24:38', '29', '29', ''), ('1305', '107', '46', '2016', '', null, '', null, '1', '2016-04-10 18:24:38', '29', '29', ''), ('1306', '121', '49', '2014', '', null, '', null, '1', '2016-04-10 19:58:22', '29', '29', ''), ('1307', '121', '50', '2014', '', null, '', null, '1', '2016-04-10 19:58:22', '29', '29', ''), ('1308', '121', '51', '2014', '', null, '', null, '1', '2016-04-10 19:58:22', '29', '29', ''), ('1309', '121', '48', '2014', '', null, '', null, '1', '2016-04-10 19:58:22', '29', '29', ''), ('1310', '121', '47', '2014', '', null, '', null, '1', '2016-04-