DROP TABLE IF EXISTS `ip_project_contributions`;
CREATE TABLE `ip_project_contributions` (
`id`  bigint(20) NOT NULL AUTO_INCREMENT ,
`project_id`  bigint(20) NOT NULL COMMENT 'This field is a foreign key to the table projects.' ,
`mog_id`  bigint(20) NOT NULL COMMENT 'This field is a foreign key to the table IP Elements.\nThe vales referenced in this column should be of type \'Outputs\' but this constraint is checked at application level.' ,
`midOutcome_id`  bigint(20) NOT NULL COMMENT 'This field is a foreign key to the table IP Elements.\nThe vales referenced in this column should be of type midOutcome but this constraint is checked at application level.' ,
`is_active`  tinyint(1) NOT NULL DEFAULT 1 ,
`active_since`  timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ,
`created_by`  bigint(20) NOT NULL ,
`modified_by`  bigint(20) NOT NULL ,
`modification_justification`  text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL ,
PRIMARY KEY (`id`),
FOREIGN KEY (`created_by`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`modified_by`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`mog_id`) REFERENCES `ip_elements` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`project_id`) REFERENCES `projects` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`midOutcome_id`) REFERENCES `ip_elements` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
UNIQUE INDEX `UK_project_contributions_unique` (`project_id`, `mog_id`, `midOutcome_id`) USING BTREE ,
INDEX `FK_projects_ipElements_ipElementID_idx` (`mog_id`) USING BTREE ,
INDEX `FK_projects_midOutcome_ipElementID_idx` (`midOutcome_id`) USING BTREE ,
INDEX `FK_projects_ipElements_projectID_idx` (`project_id`) USING BTREE ,
INDEX `fk_ip_project_contributions_users_created_by` (`created_by`) USING BTREE ,
INDEX `fk_ip_project_contributions_users_modified_by` (`modified_by`) USING BTREE 
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
AUTO_INCREMENT=5494

;

