-- ----------------------------
-- Table structure for `outcome_synthesis`
-- ----------------------------
DROP TABLE IF EXISTS `outcome_synthesis`;
CREATE TABLE `outcome_synthesis` (
`id`  bigint(20) NOT NULL AUTO_INCREMENT ,
`ip_progam_id`  bigint(20) NOT NULL ,
`year`  int(11) NOT NULL ,
`mid_outcome_id`  bigint(20) NOT NULL ,
`indicador_id`  bigint(20) NOT NULL ,
`achieved`  double(11,2) NULL DEFAULT NULL ,
`synthesis_anual`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`synthesis_gender`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`discrepancy`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`achieved_expected`  double(11,2) NULL DEFAULT NULL ,
PRIMARY KEY (`id`),
FOREIGN KEY (`indicador_id`) REFERENCES `ip_indicators` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
FOREIGN KEY (`ip_progam_id`) REFERENCES `ip_programs` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
FOREIGN KEY (`mid_outcome_id`) REFERENCES `ip_elements` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
INDEX `ip_progam_id` (`ip_progam_id`) USING BTREE ,
INDEX `mid_outcome_id` (`mid_outcome_id`) USING BTREE ,
INDEX `indicador_id` (`indicador_id`) USING BTREE 
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
AUTO_INCREMENT=58

;

