INDEX `fk_ip_other_contributions_users_created_by` (`created_by`) USING BTREE ,
INDEX `fk_ip_other_contributions_users_modified_by` (`modified_by`) USING BTREE 
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
AUTO_INCREMENT=191

;

