INSERT INTO `crp_indicator_reports` VALUES ('2434', 'T4.1: 0', null, 'T4.3: 29, T4.2: 0', 'T4.3: Agriculture and Climate Change Policies were analyzed in 29 countries in West, East and Southern Africa, as part of the African Agriculture and Climate Change Series.\r\n\r\nhttp://www.ifpri.org/publication/west-african-agriculture-and-climate-change\r\nhttp://www.ifpri.org/publication/east-african-agriculture-and-climate-change-0\r\nhttp://www.ifpri.org/publication/southern-african-agriculture-and-climate-change\r\n\r\nAll 29 country summaries can be found:\r\nhttp://www.ifpri.org/publications/results/taxonomy%3A933.7013', null, '5', '27', '2013', '2016-02-11 22:49:42');
INSERT INTO `crp_indicator_reports` VALUES ('2435', 'T4.3: 9, T4.2: 0', null, 'T4.1: 12, T4.2: 12', 'T4.1: Description: Policies in all UNEP WCMC/CCAFS regional policy processes (SEA, EA, LAM), plus Honduras and Bangladesh. See the scenarios case study report for details: https://www.dropbox.com/s/ldeumebx8hrdrj6/Case%20study%20CCAFS%20scenario-guided%20policy%20research%202014.docx?dl=0, T4.2: Policies in all UNEP WCMC/CCAFS regional policy processes (SEA, EA, LAM), plus Honduras and Bangladesh. See the scenarios case study report for details: https://www.dropbox.com/s/ldeumebx8hrdrj6/Case%20study%20CCAFS%20scenario-guided%20policy%20research%202014.docx?dl=0', null, '5', '27', '2014', '2016-02-11 22:49:42');
