insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('1.1',1, 'By 2030, eradicate extreme poverty for all people everywhere, currently measured as people living on less than $1.25 a day');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('1.2',1, 'By 2030, reduce at least by half the proportion of men, women and children of all ages living in poverty in all its dimensions according to national definitions');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('1.3',1, 'Implement nationally appropriate social protection systems and measures for all, including floors, and by 2030 achieve substantial coverage of the poor and the vulnerable');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('1.4',1, 'By 2030, ensure that all men and women, in particular the poor and the vulnerable, have equal rights to economic resources, as well as access to basic services, ownership and control over land and other forms of property, inheritance, natural resources, appropriate new technology and financial services, including microfinance');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('1.5',1, 'By 2030, build the resilience of the poor and those in vulnerable situations and reduce their exposure and vulnerability to climate-related extreme events and other economic, social and environmental shocks and disasters');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('1.a',1, 'Ensure significant mobilization of resources from a variety of sources, including through enhanced development cooperation, in order to provide adequate and predictable means for developing countries, in particular least developed countries, to implement programmes and policies to end poverty in all its dimensions');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('1.b',1, 'Create sound policy frameworks at the national, regional and international levels, based on pro-poor and gender-sensitive development strategies, to support accelerated investment in poverty eradication actions');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('2.1',2, 'By 2030, end hunger and ensure access by all people, in particular the poor and people in vulnerable situations, including infants, to safe, nutritious and sufficient food all year round');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('2.2',2, 'By 2030, end all forms of malnutrition, including achieving, by 2025, the internationally agreed targets on stunting and wasting in children under 5 years of age, and address the nutritional needs of adolescent girls, pregnant and lactating women and older persons');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('2.3',2, 'By 2030, double the agricultural productivity and incomes of small-scale food producers, in particular women, indigenous peoples, family farmers, pastoralists and fishers, including through secure and equal access to land, other productive resources and inputs, knowledge, financial services, markets and opportunities for value addition and non-farm employment');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('2.4',2, 'By 2030, ensure sustainable food production systems and implement resilient agricultural practices that increase productivity and production, that help maintain ecosystems, that strengthen capacity for adaptation to climate change, extreme weather, drought, flooding and other disasters and that progressively improve land and soil quality');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('2.5',2, 'By 2020, maintain the genetic diversity of seeds, cultivated plants and farmed and domesticated animals and their related wild species, including through soundly managed and diversified seed and plant banks at the national, regional and international levels, and promote access to and fair and equitable sharing of benefits arising from the utilization of genetic resources and associated traditional knowledge, as internationally agreed');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('2.a',2, 'Increase investment, including through enhanced international cooperation, in rural infrastructure, agricultural research and extension services, technology development and plant and livestock gene banks in order to enhance agricultural productive capacity in developing countries, in particular least developed countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('2.b',2, 'Correct and prevent trade restrictions and distortions in world agricultural markets, including through the parallel elimination of all forms of agricultural export subsidies and all export measures with equivalent effect, in accordance with the mandate of the Doha Development Round');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('2.c',2, 'Adopt measures to ensure the proper functioning of food commodity markets and their derivatives and facilitate timely access to market information, including on food reserves, in order to help limit extreme food price volatility');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.1',3, 'By 2030, reduce the global maternal mortality ratio to less than 70 per 100,000 live births');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.2',3, 'By 2030, end preventable deaths of newborns and children under 5 years of age, with all countries aiming to reduce neonatal mortality to at least as low as 12 per 1,000 live births and under-5 mortality to at least as low as 25 per 1,000 live births');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.3',3, 'By 2030, end the epidemics of AIDS, tuberculosis, malaria and neglected tropical diseases and combat hepatitis, water-borne diseases and other communicable diseases');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.4',3, 'By 2030, reduce by one third premature mortality from non-communicable diseases through prevention and treatment and promote mental health and well-being');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.5',3, 'Strengthen the prevention and treatment of substance abuse, including narcotic drug abuse and harmful use of alcohol');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.6',3, 'By 2020, halve the number of global deaths and injuries from road traffic accidents');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.7',3, 'By 2030, ensure universal access to sexual and reproductive health-care services, including for family planning, information and education, and the integration of reproductive health into national strategies and programmes');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.8',3, 'Achieve universal health coverage, including financial risk protection, access to quality essential health-care services and access to safe, effective, quality and affordable essential medicines and vaccines for all');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.9',3, 'By 2030, substantially reduce the number of deaths and illnesses from hazardous chemicals and air, water and soil pollution and contamination');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.a',3, 'Strengthen the implementation of the World Health Organization Framework Convention on Tobacco Control in all countries, as appropriate');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.b',3, 'Support the research and development of vaccines and medicines for the communicable and non‑communicable diseases that primarily affect developing countries, provide access to affordable essential medicines and vaccines, in accordance with the Doha Declaration on the TRIPS Agreement and Public Health, which affirms the right of developing countries to use to the full the provisions in the Agreement on Trade-Related Aspects of Intellectual Property Rights regarding flexibilities to protect public health, and, in particular, provide access to medicines for all');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.c',3, 'Substantially increase health financing and the recruitment, development, training and retention of the health workforce in developing countries, especially in least developed countries and small island developing States');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('3.d',3, 'Strengthen the capacity of all countries, in particular developing countries, for early warning, risk reduction and management of national and global health risks');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.1',4, 'By 2030, ensure that all girls and boys complete free, equitable and quality primary and secondary education leading to relevant and effective learning outcomes');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.2',4, 'By 2030, ensure that all girls and boys have access to quality early childhood development, care and pre-primary education so that they are ready for primary education');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.3',4, 'By 2030, ensure equal access for all women and men to affordable and quality technical, vocational and tertiary education, including university');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.4',4, 'By 2030, substantially increase the number of youth and adults who have relevant skills, including technical and vocational skills, for employment, decent jobs and entrepreneurship');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.5',4, 'By 2030, eliminate gender disparities in education and ensure equal access to all levels of education and vocational training for the vulnerable, including persons with disabilities, indigenous peoples and children in vulnerable situations');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.6',4, 'By 2030, ensure that all youth and a substantial proportion of adults, both men and women, achieve literacy and numeracy');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.7',4, 'By 2030, ensure that all learners acquire the knowledge and skills needed to promote sustainable development, including, among others, through education for sustainable development and sustainable lifestyles, human rights, gender equality, promotion of a culture of peace and non-violence, global citizenship and appreciation of cultural diversity and of culture’s contribution to sustainable development');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.a',4, 'Build and upgrade education facilities that are child, disability and gender sensitive and provide safe, non-violent, inclusive and effective learning environments for all');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.b',4, 'By 2020, substantially expand globally the number of scholarships available to developing countries, in particular least developed countries, small island developing States and African countries, for enrolment in higher education, including vocational training and information and communications technology, technical, engineering and scientific programmes, in developed countries and other developing countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('4.c',4, 'By 2030, substantially increase the supply of qualified teachers, including through international cooperation for teacher training in developing countries, especially least developed countries and small island developing States');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.1',5, 'End all forms of discrimination against all women and girls everywhere');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.2',5, 'Eliminate all forms of violence against all women and girls in the public and private spheres, including trafficking and sexual and other types of exploitation');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.3',5, 'Eliminate all harmful practices, such as child, early and forced marriage and female genital mutilation');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.4',5, 'Recognize and value unpaid care and domestic work through the provision of public services, infrastructure and social protection policies and the promotion of shared responsibility within the household and the family as nationally appropriate');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.5',5, 'Ensure women’s full and effective participation and equal opportunities for leadership at all levels of decision-making in political, economic and public life');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.6',5, 'Ensure universal access to sexual and reproductive health and reproductive rights as agreed in accordance with the Programme of Action of the International Conference on Population and Development and the Beijing Platform for Action and the outcome documents of their review conferences');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.a',5, 'Undertake reforms to give women equal rights to economic resources, as well as access to ownership and control over land and other forms of property, financial services, inheritance and natural resources, in accordance with national laws');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.b',5, 'Enhance the use of enabling technology, in particular information and communications technology, to promote the empowerment of women');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('5.c',5, 'Adopt and strengthen sound policies and enforceable legislation for the promotion of gender equality and the empowerment of all women and girls at all levels');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('6.1',6, 'By 2030, achieve universal and equitable access to safe and affordable drinking water for all');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('6.2',6, 'By 2030, achieve access to adequate and equitable sanitation and hygiene for all and end open defecation, paying special attention to the needs of women and girls and those in vulnerable situations');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('6.3',6, 'By 2030, improve water quality by reducing pollution, eliminating dumping and minimizing release of hazardous chemicals and materials, halving the proportion of untreated wastewater and substantially increasing recycling and safe reuse globally');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('6.4',6, 'By 2030, substantially increase water-use efficiency across all sectors and ensure sustainable withdrawals and supply of freshwater to address water scarcity and substantially reduce the number of people suffering from water scarcity');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('6.5',6, 'By 2030, implement integrated water resources management at all levels, including through transboundary cooperation as appropriate');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('6.6',6, 'By 2020, protect and restore water-related ecosystems, including mountains, forests, wetlands, rivers, aquifers and lakes');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('6.a',6, 'By 2030, expand international cooperation and capacity-building support to developing countries in water- and sanitation-related activities and programmes, including water harvesting, desalination, water efficiency, wastewater treatment, recycling and reuse technologies');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('6.b',6, 'Support and strengthen the participation of local communities in improving water and sanitation management');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('7.1',7, 'By 2030, ensure universal access to affordable, reliable and modern energy services');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('7.2',7, 'By 2030, increase substantially the share of renewable energy in the global energy mix');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('7.3',7, 'By 2030, double the global rate of improvement in energy efficiency');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('7.a',7, 'By 2030, enhance international cooperation to facilitate access to clean energy research and technology, including renewable energy, energy efficiency and advanced and cleaner fossil-fuel technology, and promote investment in energy infrastructure and clean energy technology');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('7.b',7, 'By 2030, expand infrastructure and upgrade technology for supplying modern and sustainable energy services for all in developing countries, in particular least developed countries, small island developing States and landlocked developing countries, in accordance with their respective programmes of support');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.1',8, 'Sustain per capita economic growth in accordance with national circumstances and, in particular, at least 7 per cent gross domestic product growth per annum in the least developed countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.2',8, 'Achieve higher levels of economic productivity through diversification, technological upgrading and innovation, including through a focus on high-value added and labour-intensive sectors');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.3',8, 'Promote development-oriented policies that support productive activities, decent job creation, entrepreneurship, creativity and innovation, and encourage the formalization and growth of micro-, small- and medium-sized enterprises, including through access to financial services');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.4',8, 'Improve progressively, through 2030, global resource efficiency in consumption and production and endeavour to decouple economic growth from environmental degradation, in accordance with the 10‑Year Framework of Programmes on Sustainable Consumption and Production, with developed countries taking the lead');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.5',8, 'By 2030, achieve full and productive employment and decent work for all women and men, including for young people and persons with disabilities, and equal pay for work of equal value');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.6',8, 'By 2020, substantially reduce the proportion of youth not in employment, education or training');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.7',8, 'Take immediate and effective measures to eradicate forced labour, end modern slavery and human trafficking and secure the prohibition and elimination of the worst forms of child labour, including recruitment and use of child soldiers, and by 2025 end child labour in all its forms');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.8',8, 'Protect labour rights and promote safe and secure working environments for all workers, including migrant workers, in particular women migrants, and those in precarious employment');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.9',8, 'By 2030, devise and implement policies to promote sustainable tourism that creates jobs and promotes local culture and products');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.1',8, '0 Strengthen the capacity of domestic financial institutions to encourage and expand access to banking, insurance and financial services for all');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.a',8, 'Increase Aid for Trade support for developing countries, in particular least developed countries, including through the Enhanced Integrated Framework for Trade-related Technical Assistance to Least Developed Countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('8.b',8, 'By 2020, develop and operationalize a global strategy for youth employment and implement the Global Jobs Pact of the International Labour Organization');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('9.1',9, 'Develop quality, reliable, sustainable and resilient infrastructure, including regional and trans-border infrastructure, to support economic development and human well-being, with a focus on affordable and equitable access for all');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('9.2',9, 'Promote inclusive and sustainable industrialization and, by 2030, significantly raise industry’s share of employment and gross domestic product, in line with national circumstances, and double its share in least developed countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('9.3',9, 'Increase the access of small-scale industrial and other enterprises, in particular in developing countries, to financial services, including affordable credit, and their integration into value chains and markets');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('9.4',9, 'By 2030, upgrade infrastructure and retrofit industries to make them sustainable, with increased resource-use efficiency and greater adoption of clean and environmentally sound technologies and industrial processes, with all countries taking action in accordance with their respective capabilities');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('9.5',9, 'Enhance scientific research, upgrade the technological capabilities of industrial sectors in all countries, in particular developing countries, including, by 2030, encouraging innovation and substantially increasing the number of research and development workers per 1 million people and public and private research and development spending');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('9.a',9, 'Facilitate sustainable and resilient infrastructure development in developing countries through enhanced financial, technological and technical support to African countries, least developed countries, landlocked developing countries and small island developing States');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('9.b',9, 'Support domestic technology development, research and innovation in developing countries, including by ensuring a conducive policy environment for, inter alia, industrial diversification and value addition to commodities');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('9.c',9, 'Significantly increase access to information and communications technology and strive to provide universal and affordable access to the Internet in least developed countries by 2020');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.1',10, 'By 2030, progressively achieve and sustain income growth of the bottom 40 per cent of the population at a rate higher than the national average');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.2',10, 'By 2030, empower and promote the social, economic and political inclusion of all, irrespective of age, sex, disability, race, ethnicity, origin, religion or economic or other status');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.3',10, 'Ensure equal opportunity and reduce inequalities of outcome, including by eliminating discriminatory laws, policies and practices and promoting appropriate legislation, policies and action in this regard');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.4',10, 'Adopt policies, especially fiscal, wage and social protection policies, and progressively achieve greater equality');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.5',10, 'Improve the regulation and monitoring of global financial markets and institutions and strengthen the implementation of such regulations');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.6',10, 'Ensure enhanced representation and voice for developing countries in decision-making in global international economic and financial institutions in order to deliver more effective, credible, accountable and legitimate institutions');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.7',10, 'Facilitate orderly, safe, regular and responsible migration and mobility of people, including through the implementation of planned and well-managed migration policies');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.a',10, 'Implement the principle of special and differential treatment for developing countries, in particular least developed countries, in accordance with World Trade Organization agreements');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.b',10, 'Encourage official development assistance and financial flows, including foreign direct investment, to States where the need is greatest, in particular least developed countries, African countries, small island developing States and landlocked developing countries, in accordance with their national plans and programmes');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('10.c',10, 'By 2030, reduce to less than 3 per cent the transaction costs of migrant remittances and eliminate remittance corridors with costs higher than 5 per cent');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.1',11, 'By 2030, ensure access for all to adequate, safe and affordable housing and basic services and upgrade slums');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.2',11, 'By 2030, provide access to safe, affordable, accessible and sustainable transport systems for all, improving road safety, notably by expanding public transport, with special attention to the needs of those in vulnerable situations, women, children, persons with disabilities and older persons');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.3',11, 'By 2030, enhance inclusive and sustainable urbanization and capacity for participatory, integrated and sustainable human settlement planning and management in all countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.4',11, 'Strengthen efforts to protect and safeguard the world’s cultural and natural heritage');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.5',11, 'By 2030, significantly reduce the number of deaths and the number of people affected and substantially decrease the direct economic losses relative to global gross domestic product caused by disasters, including water-related disasters, with a focus on protecting the poor and people in vulnerable situations');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.6',11, 'By 2030, reduce the adverse per capita environmental impact of cities, including by paying special attention to air quality and municipal and other waste management');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.7',11, 'By 2030, provide universal access to safe, inclusive and accessible, green and public spaces, in particular for women and children, older persons and persons with disabilities');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.a',11, 'Support positive economic, social and environmental links between urban, peri-urban and rural areas by strengthening national and regional development planning');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.b',11, 'By 2020, substantially increase the number of cities and human settlements adopting and implementing integrated policies and plans towards inclusion, resource efficiency, mitigation and adaptation to climate change, resilience to disasters, and develop and implement, in line with the Sendai Framework for Disaster Risk Reduction 2015-2030, holistic disaster risk management at all levels');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('11.c',11, 'Support least developed countries, including through financial and technical assistance, in building sustainable and resilient buildings utilizing local materials');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.1',12, 'Implement the 10-Year Framework of Programmes on Sustainable Consumption and Production Patterns, all countries taking action, with developed countries taking the lead, taking into account the development and capabilities of developing countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.2',12, 'By 2030, achieve the sustainable management and efficient use of natural resources');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.3',12, 'By 2030, halve per capita global food waste at the retail and consumer levels and reduce food losses along production and supply chains, including post-harvest losses');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.4',12, 'By 2020, achieve the environmentally sound management of chemicals and all wastes throughout their life cycle, in accordance with agreed international frameworks, and significantly reduce their release to air, water and soil in order to minimize their adverse impacts on human health and the environment');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.5',12, 'By 2030, substantially reduce waste generation through prevention, reduction, recycling and reuse');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.6',12, 'Encourage companies, especially large and transnational companies, to adopt sustainable practices and to integrate sustainability information into their reporting cycle');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.7',12, 'Promote public procurement practices that are sustainable, in accordance with national policies and priorities');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.8',12, 'By 2030, ensure that people everywhere have the relevant information and awareness for sustainable development and lifestyles in harmony with nature');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.a',12, 'Support developing countries to strengthen their scientific and technological capacity to move towards more sustainable patterns of consumption and production');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.b',12, 'Develop and implement tools to monitor sustainable development impacts for sustainable tourism that creates jobs and promotes local culture and products');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('12.c',12, 'Rationalize inefficient fossil-fuel subsidies that encourage wasteful consumption by removing market distortions, in accordance with national circumstances, including by restructuring taxation and phasing out those harmful subsidies, where they exist, to reflect their environmental impacts, taking fully into account the specific needs and conditions of developing countries and minimizing the possible adverse impacts on their development in a manner that protects the poor and the affected communities');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('13.1',13, 'Strengthen resilience and adaptive capacity to climate-related hazards and natural disasters in all countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('13.2',13, 'Integrate climate change measures into national policies, strategies and planning');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('13.3',13, 'Improve education, awareness-raising and human and institutional capacity on climate change mitigation, adaptation, impact reduction and early warning');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('13.a',13, 'Implement the commitment undertaken by developed-country parties to the United Nations Framework Convention on Climate Change to a goal of mobilizing jointly $100 billion annually by 2020 from all sources to address the needs of developing countries in the context of meaningful mitigation actions and transparency on implementation and fully operationalize the Green Climate Fund through its capitalization as soon as possible');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('13.b',13, 'Promote mechanisms for raising capacity for effective climate change-related planning and management in least developed countries and small island developing States, including focusing on women, youth and local and marginalized communities');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.1',14, 'By 2025, prevent and significantly reduce marine pollution of all kinds, in particular from land-based activities, including marine debris and nutrient pollution');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.2',14, 'By 2020, sustainably manage and protect marine and coastal ecosystems to avoid significant adverse impacts, including by strengthening their resilience, and take action for their restoration in order to achieve healthy and productive oceans');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.3',14, 'Minimize and address the impacts of ocean acidification, including through enhanced scientific cooperation at all levels');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.4',14, 'By 2020, effectively regulate harvesting and end overfishing, illegal, unreported and unregulated fishing and destructive fishing practices and implement science-based management plans, in order to restore fish stocks in the shortest time feasible, at least to levels that can produce maximum sustainable yield as determined by their biological characteristics');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.5',14, 'By 2020, conserve at least 10 per cent of coastal and marine areas, consistent with national and international law and based on the best available scientific information');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.6',14, 'By 2020, prohibit certain forms of fisheries subsidies which contribute to overcapacity and overfishing, eliminate subsidies that contribute to illegal, unreported and unregulated fishing and refrain from introducing new such subsidies, recognizing that appropriate and effective special and differential treatment for developing and least developed countries should be an integral part of the World Trade Organization fisheries subsidies negotiation[b]');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.7',14, 'By 2030, increase the economic benefits to small island developing States and least developed countries from the sustainable use of marine resources, including through sustainable management of fisheries, aquaculture and tourism');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.a',14, 'Increase scientific knowledge, develop research capacity and transfer marine technology, taking into account the Intergovernmental Oceanographic Commission Criteria and Guidelines on the Transfer of Marine Technology, in order to improve ocean health and to enhance the contribution of marine biodiversity to the development of developing countries, in particular small island developing States and least developed countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.b',14, 'Provide access for small-scale artisanal fishers to marine resources and markets');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('14.c',14, 'Enhance the conservation and sustainable use of oceans and their resources by implementing international law as reflected in the United Nations Convention on the Law of the Sea, which provides the legal framework for the conservation and sustainable use of oceans and their resources, as recalled in paragraph 158 of “The future we want”');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.1',15, 'By 2020, ensure the conservation, restoration and sustainable use of terrestrial and inland freshwater ecosystems and their services, in particular forests, wetlands, mountains and drylands, in line with obligations under international agreements');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.2',15, 'By 2020, promote the implementation of sustainable management of all types of forests, halt deforestation, restore degraded forests and substantially increase afforestation and reforestation globally');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.3',15, 'By 2030, combat desertification, restore degraded land and soil, including land affected by desertification, drought and floods, and strive to achieve a land degradation-neutral world');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.4',15, 'By 2030, ensure the conservation of mountain ecosystems, including their biodiversity, in order to enhance their capacity to provide benefits that are essential for sustainable development');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.5',15, 'Take urgent and significant action to reduce the degradation of natural habitats, halt the loss of biodiversity and, by 2020, protect and prevent the extinction of threatened species');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.6',15, 'Promote fair and equitable sharing of the benefits arising from the utilization of genetic resources and promote appropriate access to such resources, as internationally agreed');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.7',15, 'Take urgent action to end poaching and trafficking of protected species of flora and fauna and address both demand and supply of illegal wildlife products');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.8',15, 'By 2020, introduce measures to prevent the introduction and significantly reduce the impact of invasive alien species on land and water ecosystems and control or eradicate the priority species');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.9',15, 'By 2020, integrate ecosystem and biodiversity values into national and local planning, development processes, poverty reduction strategies and accounts');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.a',15, 'Mobilize and significantly increase financial resources from all sources to conserve and sustainably use biodiversity and ecosystems');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.b',15, 'Mobilize significant resources from all sources and at all levels to finance sustainable forest management and provide adequate incentives to developing countries to advance such management, including for conservation and reforestation');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('15.c',15, 'Enhance global support for efforts to combat poaching and trafficking of protected species, including by increasing the capacity of local communities to pursue sustainable livelihood opportunities');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.1',16, 'Significantly reduce all forms of violence and related death rates everywhere');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.2',16, 'End abuse, exploitation, trafficking and all forms of violence against and torture of children');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.3',16, 'Promote the rule of law at the national and international levels and ensure equal access to justice for all');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.4',16, 'By 2030, significantly reduce illicit financial and arms flows, strengthen the recovery and return of stolen assets and combat all forms of organized crime');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.5',16, 'Substantially reduce corruption and bribery in all their forms');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.6',16, 'Develop effective, accountable and transparent institutions at all levels');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.7',16, 'Ensure responsive, inclusive, participatory and representative decision-making at all levels');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.8',16, 'Broaden and strengthen the participation of developing countries in the institutions of global governance');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.9',16, 'By 2030, provide legal identity for all, including birth registration');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.1',16, '0 Ensure public access to information and protect fundamental freedoms, in accordance with national legislation and international agreements');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.a',16, 'Strengthen relevant national institutions, including through international cooperation, for building capacity at all levels, in particular in developing countries, to prevent violence and combat terrorism and crime');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('16.b',16, 'Promote and enforce non-discriminatory laws and policies for sustainable development');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.1',17, 'Strengthen domestic resource mobilization, including through international support to developing countries, to improve domestic capacity for tax and other revenue collection');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.2',17, 'Developed countries to implement fully their official development assistance commitments, including the commitment by many developed countries to achieve the target of 0.7 per cent of gross national income for official development assistance (ODA/GNI) to developing countries and 0.15 to 0.20 per cent of ODA/GNI to least developed countries; ODA providers are encouraged to consider setting a target to provide at least 0.20 per cent of ODA/GNI to least developed countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.3',17, 'Mobilize additional financial resources for developing countries from multiple sources');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.4',17, 'Assist developing countries in attaining long-term debt sustainability through coordinated policies aimed at fostering debt financing, debt relief and debt restructuring, as appropriate, and address the external debt of highly indebted poor countries to reduce debt distress');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.5',17, 'Adopt and implement investment promotion regimes for least developed countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.6',17, 'Enhance North-South, South-South and triangular regional and international cooperation on and access to science, technology and innovation and enhance knowledge-sharing on mutually agreed terms, including through improved coordination among existing mechanisms, in particular at the United Nations level, and through a global technology facilitation mechanism');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.7',17, 'Promote the development, transfer, dissemination and diffusion of environmentally sound technologies to developing countries on favourable terms, including on concessional and preferential terms, as mutually agreed');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.8',17, 'Fully operationalize the technology bank and science, technology and innovation capacity-building mechanism for least developed countries by 2017 and enhance the use of enabling technology, in particular information and communications technology');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.9',17, 'Enhance international support for implementing effective and targeted capacity-building in developing countries to support national plans to implement all the Sustainable Development Goals, including through North-South, South-South and triangular cooperation');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.1',17, 'Promote a universal, rules-based, open, non‑discriminatory and equitable multilateral trading system under the World Trade Organization, including through the conclusion of negotiations under its Doha Development Agenda');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.11',17, 'Significantly increase the exports of developing countries, in particular with a view to doubling the least developed countries’ share of global exports by 2020');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.12',17, 'Realize timely implementation of duty-free and quota-free market access on a lasting basis for all least developed countries, consistent with World Trade Organization decisions, including by ensuring that preferential rules of origin applicable to imports from least developed countries are transparent and simple, and contribute to facilitating market access');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.13',17, 'Enhance global macroeconomic stability, including through policy coordination and policy coherence');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.14',17, 'Enhance policy coherence for sustainable development');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.15',17, 'Respect each country’s policy space and leadership to establish and implement policies for poverty eradication and sustainable development');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.16',17, 'Enhance the Global Partnership for Sustainable Development, complemented by multi-stakeholder partnerships that mobilize and share knowledge, expertise, technology and financial resources, to support the achievement of the Sustainable Development Goals in all countries, in particular developing countries');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.17',17, 'Encourage and promote effective public, public-private and civil society partnerships, building on the experience and resourcing strategies of partnerships');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.18',17, 'By 2020, enhance capacity-building support to developing countries, including for least developed countries and small island developing States, to increase significantly the availability of high-quality, timely and reliable data disaggregated by income, gender, age, race, ethnicity, migratory status, disability, geographic location and other characteristics relevant in national contexts');
insert into sdg_targets(sdg_target_code, sdg_id, sdg_target) values('17.19',17, 'By 2030, build on existing initiatives to develop measurements of progress on sustainable development that complement gross domestic product, and support statistical capacity-building in developing countries');
