CREATE TABLE IF NOT EXISTS report_synthesis_peer_review_papers (
  id BIGINT(20) NOT NULL AUTO_INCREMENT,
  citation TEXT NULL,
  url VARCHAR(60) NULL,
  is_ISI_journal TINYINT NULL,
  is_open_access TINYINT NULL,
  phase_id BIGINT(20) NOT NULL,
  PRIMARY KEY (id),
  INDEX fk_report_synthesis_peer_review_papers_phases1_idx (phase_id ASC),
  CONSTRAINT fk_report_synthesis_peer_review_papers_phases1
    FOREIGN KEY (phase_id)
    REFERENCES phases (id)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB
;


/* ALMETRICS TABLE*/
CREATE TABLE IF NOT EXISTS report_synthesis_altmetrics (
  id BIGINT(20) NOT NULL AUTO_INCREMENT,
  attention_score INT NULL,
  title TEXT NULL,
  journal_title TEXT NULL,
  author TEXT NULL,
  publication_date DATE NULL,
  doi VARCHAR(50) NULL,
  detail_url VARCHAR(50) NULL,
  news_total INT NULL,
  blog_total INT NULL,
  policy_total INT NULL,
  twitter_total INT NULL,
  facebook_total INT NULL,
  wikipedia_total INT NULL,
  mendeley_total INT NULL,
  id_phase BIGINT(20) NOT NULL,
  PRIMARY KEY (id),
  INDEX fk_report_synthesis_altmetrics_phases1_idx (id_phase ASC),
  CONSTRAINT fk_report_synthesis_altmetrics_phases1
    FOREIGN KEY (id_phase)
    REFERENCES phases (id)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB;

