-- ----------------------------
-- Table structure for `projects`
-- ----------------------------
DROP TABLE IF EXISTS `projects`;
CREATE TABLE `projects` (
`id`  bigint(20) NOT NULL AUTO_INCREMENT ,
`title`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`summary`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`start_date`  date NULL DEFAULT NULL ,
`end_date`  date NULL DEFAULT NULL ,
`type`  enum('CCAFS_CORE','CCAFS_COFUNDED','BILATERAL') CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'CCAFS_CORE' ,
`is_global`  tinyint(1) NOT NULL DEFAULT 0 ,
`is_cofinancing`  tinyint(1) NOT NULL DEFAULT 0 ,
`leader_responsabilities`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`liaison_institution_id`  bigint(20) NULL DEFAULT NULL ,
`liaison_user_id`  bigint(20) NULL DEFAULT NULL COMMENT 'foreign key to the table liaison_users' ,
`requires_workplan_upload`  tinyint(1) NULL DEFAULT 0 ,
`workplan_name`  varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL ,
`bilateral_contract_name`  varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL ,
`created_by`  bigint(20) NULL DEFAULT NULL COMMENT 'foreign key to the table users' ,
`is_active`  tinyint(1) NOT NULL DEFAULT 1 ,
`active_since`  timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ,
`modified_by`  bigint(20) NOT NULL ,
`modification_justification`  text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL ,
`annual_report_to_dornor`  varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL ,
`crp_id`  bigint(20) NOT NULL ,
PRIMARY KEY (`id`),
FOREIGN KEY (`created_by`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`modified_by`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`liaison_user_id`) REFERENCES `liaison_users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`liaison_institution_id`) REFERENCES `liaison_institutions` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`crp_id`) REFERENCES `crps` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
INDEX `FK_projects_liaison_liaison_users_idx` (`liaison_user_id`) USING BTREE ,
INDEX `fk_projects_employees_created_by` (`created_by`) USING BTREE ,
INDEX `fk_projects_users_modified_by` (`modified_by`) USING BTREE ,
INDEX `institution_id` (`liaison_institution_id`) USING BTREE ,
INDEX `crp_id` (`crp_id`) USING BTREE 
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
AUTO_INCREMENT=248

