

-- ----------------------------
-- Table structure for `project_partner_persons`
-- ----------------------------
DROP TABLE IF EXISTS `project_partner_persons`;
CREATE TABLE `project_partner_persons` (
`id`  bigint(20) NOT NULL AUTO_INCREMENT ,
`project_partner_id`  bigint(20) NOT NULL ,
`user_id`  bigint(20) NOT NULL ,
`contact_type`  enum('PL','PC','CP') CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL ,
`responsibilities`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`is_active`  tinyint(1) NOT NULL DEFAULT 1 ,
`active_since`  timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ,
`created_by`  bigint(20) NOT NULL ,
`modified_by`  bigint(20) NOT NULL ,
`modification_justification`  text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL ,
PRIMARY KEY (`id`),
FOREIGN KEY (`project_partner_id`) REFERENCES `project_partners` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`created_by`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (`modified_by`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
INDEX `FK_project_partner_persons_project_partners_idx` (`project_partner_id`) USING BTREE ,
INDEX `FK_project_partner_persons_users_idx` (`user_id`) USING BTREE ,
INDEX `fk_project_partners_users_created_by_idx` (`created_by`) USING BTREE ,
INDEX `fk_project_partners_users_modified_by_idx` (`modified_by`) USING BTREE 
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
AUTO_INCREMENT=1241

;

